create sequence hibernate_sequence start 1 increment 1;
create table tb_address (id int8 generated by default as identity, cep varchar(255), city varchar(255), complement varchar(255), district varchar(255), number int4, street varchar(255), client_id varchar(255), primary key (id));
create table tb_client (id varchar(255) not null, name varchar(255), surname varchar(255), primary key (id));
create table tb_client_product (product_id int8 not null, client_id varchar(255) not null);
create table tb_product (id int8 generated by default as identity, name varchar(255), price float8, url_image varchar(255), primary key (id));
create table tb_product_item (id int8 not null, quantity int4, subtotal float8, client_id varchar(255), product_id int8, primary key (id));
create table tb_purchase (id int8 generated by default as identity, cep varchar(255), city varchar(255), client_name varchar(255), client_surname varchar(255), complement varchar(255), district varchar(255), id_address int8, id_client varchar(255), id_item int8, id_product int8, name varchar(255), number int4, price float8, qr_code_url varchar(255), quantity int4, status int4, street varchar(255), subtotal float8, url_image varchar(255), primary key (id));
